const mysql = require("mysql")
class Database {
	constructor(host = "localhost", user = "root", database = "stock"){
		this.connection = mysql.createConnection({
			host: host,
			user: user,
			database: database
		});
	}
	query(sql, args){
		return new Promise((resolve, reject) => {
			this.connection.query(sql, args, (err, rows)=> {
				if(err) return reject(err);
				resolve(rows);
			});
		});
	}
	async moveFirstRowToEnd(){
		var row = await this.query(`select * from stockQuotes order by id`);
		let id = row[0].id;
		let quote = row[0].quote;
		await this.query(`delete from stockQuotes where id = ${id}`);
		await this.query(`insert into stockQuotes (quote) values("${quote}")`);
	}
	close(){
		return new Promise((resolve, reject) => {
			this.connection.end( err => {
				if(err) return reject(err)
				resolve();
			});
		});
	}
}
module.exports = Database